import * as conf from './conf'
import { playerSprites } from './images'
import * as img from './images'
import { TileType, State } from './state'


export const render = (ctx: CanvasRenderingContext2D) => (state: State) => {
  const tileSize = conf.TILESIZE
  ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height)

  for (let y = 0; y < state.gameMap.height; y++) {
    for (let x = 0; x < state.gameMap.width; x++) {
      const tile = state.gameMap.tiles[y][x]

      const px = x * tileSize
      const py = y * tileSize

      switch (tile) {
        case TileType.WALL:
          ctx.drawImage(img.wallImage, px, py, tileSize, tileSize)
          break
        case TileType.BREAKABLE:
          ctx.drawImage(img.breakableImage, px, py, tileSize, tileSize)
          break
        case TileType.WATER:
          ctx.drawImage(img.waterImage, px, py, tileSize, tileSize)
          break
        case TileType.EMPTY:
          ctx.fillStyle = '#dedede'
          ctx.fillRect(px, py, tileSize, tileSize)
          break
        case TileType.EXPLOSION:
          ctx.drawImage(img.explosionImage, px, py, tileSize, tileSize)
          break
      }
    }
  }

  // Afficher
  ctx.fillStyle = 'purple'
  ctx.font = '16px Arial'
  ctx.fillText(`Level: ${state.level} , `, conf.TILESIZE, (conf.HEIGHT + 1) * (tileSize + 1))
  ctx.fillText(`Time: ${Math.ceil(state.levelTimer / 60)}s`, 10, ctx.canvas.height - 30)

  // Render player
  const playerImg = playerSprites[state.player.direction]
  if (playerImg.complete) {
    ctx.drawImage(
      playerImg,
      state.player.x * tileSize,
      state.player.y * tileSize,
      tileSize,
      tileSize
    )
  }

  // Render bombs
  state.bombs.forEach(bomb => {
    ctx.drawImage(img.bombImage, bomb.x * tileSize, bomb.y * tileSize, tileSize, tileSize)
  })
  // Render explosions
  state.explosions.forEach(explosion => {
    ctx.drawImage(img.explosionImage, explosion.x * tileSize, explosion.y * tileSize, tileSize, tileSize)

  })
  // Render enemies
  state.enemies.forEach(enemy => {
    if (!enemy.alive) return
    ctx.drawImage(img.enemyImage, enemy.x * tileSize, enemy.y * tileSize, tileSize, tileSize)
  })
  // Render PowerUps
  state.powerups.forEach(p => {
    if (p.type === 'bomb') ctx.drawImage(img.powerupBombImage, p.x * tileSize, p.y * tileSize, tileSize, tileSize)
    else if (p.type === 'range') ctx.drawImage(img.powerupRangeImage, p.x * tileSize, p.y * tileSize, tileSize, tileSize)
    else if (p.type === 'freeze') ctx.drawImage(img.powerupFreezeImage, p.x * tileSize, p.y * tileSize, tileSize, tileSize)
  })
  if (state.gameOver) {
    ctx.fillStyle = 'black'
    ctx.font = '30px Arial'
    ctx.fillText('Game Over', ctx.canvas.width / 2 - 70, ctx.canvas.height / 2)
  }
  if (!state.gameStarted){
    ctx.fillStyle = 'black'
    ctx.font = '30px Arial'
    ctx.fillText('Press any key to start', ctx.canvas.width / 2 - 70, ctx.canvas.height / 2)
    return
  }
  if (state.victory) {
    ctx.fillStyle = 'gold'
    ctx.font = '50px Arial'
    ctx.fillText('YOU WIN', ctx.canvas.width / 2 - 70, ctx.canvas.height / 2)
  }

}
