import { useEffect, useRef } from 'react'
import { generateMap, isWalkable, State } from './state'
import { render } from './renderer'

const initCanvas =
  (iterate: (ctx: CanvasRenderingContext2D) => void) =>
  (canvas: HTMLCanvasElement) => {
    const ctx = canvas.getContext('2d')
    if (!ctx) return
    requestAnimationFrame(() => iterate(ctx))
  }

const Canvas = ({ height, width }: { height: number; width: number }) => {
  const initialState: State = {
    player: {
      x: 1,
      y: 1,
      alive: true,
      bombs: 1,
      bombRange: 2,
      direction: 'right'
    },
    bombs: [],
    explosions: [],
    enemies: [],
    gameMap: {
      width: 13,
      height: 11,
      tiles: generateMap(13, 11),
    },
    gameOver: false,
    victory: false
  }

  const ref = useRef<any>()
  const state = useRef<State>(initialState)

  const handleKeyDown = (e: KeyboardEvent) => {
    const {x, y} = state.current.player
    let newX = x
    let newY = y
    switch (e.key) {
      case 'ArrowUp':
      case 'z':
        state.current.player.direction = 'up'
        newY--
        break
      case 'ArrowDown':
      case 's':
        state.current.player.direction = 'down'
        newY++
        break
      case 'ArrowLeft':
      case 'q':
        state.current.player.direction = 'left'
        newX--
        break
      case 'ArrowRight':
      case 'd':
        state.current.player.direction = 'right'
        newX++
        break
    }

    if (newX >= 0 && newX < state.current.gameMap.width && newY >= 0 && newY < state.current.gameMap.height) {
      const tile = state.current.gameMap.tiles[newY][newX]
      if (isWalkable(tile)) {
        state.current.player.x = newX
        state.current.player.y = newY
      }
    }
  }

  const iterate = (ctx: CanvasRenderingContext2D) => {
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height)
    // Render the game state
    render(ctx)(state.current)
    requestAnimationFrame(() => iterate(ctx))
  }

  useEffect(() => {
    if (ref.current) {
      initCanvas(iterate)(ref.current)
    }
    return () => {}
  }, [])
  return <canvas {...{ height, width, ref }} />
}

export default Canvas
