import * as conf from './conf'
import { playerSprites } from './images'
import * as img from './images'
import { TileType, State } from './state'


export const render = (ctx: CanvasRenderingContext2D) => (state: State) => {
  const tileSize = conf.TILESIZE
  ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height)
  for (let y = 0; y < state.gameMap.height; y++) {
    for (let x = 0; x < state.gameMap.width; x++) {
      const tile = state.gameMap.tiles[y][x]

      const px = x * tileSize
      const py = y * tileSize

      switch (tile) {
        case TileType.WALL:
          ctx.drawImage(img.wallImage, px, py, tileSize,tileSize)
          break
        case TileType.BREAKABLE:
          ctx.drawImage(img.breakableImage, px, py, tileSize,tileSize)
          break
        case TileType.WATER:
          ctx.fillStyle = 'blue'
          ctx.fillRect(px, py, tileSize, tileSize)
          break
        case TileType.EMPTY:
          ctx.fillStyle = '#dedede'
          ctx.fillRect(px, py, tileSize, tileSize)
          break
        case TileType.EXPLOSION:
          ctx.drawImage(img.explosionImage, px, py, tileSize,tileSize)
          break
      }
    }
  }

  // Render player
  const playerImg = playerSprites[state.player.direction]
  if (playerImg.complete) {
    ctx.drawImage(
      playerImg,
      state.player.x * tileSize,
      state.player.y * tileSize,
      tileSize,
      tileSize
    )
  }

  // Render bombs
  state.bombs.forEach(bomb => {
    ctx.fillStyle = 'yellow'
    ctx.fillRect(bomb.x * tileSize, bomb.y * tileSize, tileSize, tileSize)
  })
  // Render explosions
  state.explosions.forEach(explosion => {
    ctx.drawImage(img.explosionImage, explosion.x * tileSize, explosion.y * tileSize, tileSize, tileSize)

  })
  // Render enemies
  state.enemies.forEach(enemy => {
    if (!enemy.alive) return
    ctx.fillStyle = 'gray'
    ctx.fillRect(enemy.x * tileSize + 5, enemy.y * tileSize + 5, tileSize - 10, tileSize - 10)
  })
  // Render PowerUps
  state.powerups.forEach(p => {
    if (p.type === 'bomb') ctx.fillStyle = 'pink'
    else if (p.type === 'range') ctx.fillStyle = 'purple'
    else if (p.type === 'freeze') ctx.fillStyle = 'cyan'
    ctx.fillRect(p.x * tileSize + 5, p.y * tileSize + 5, tileSize - 10, tileSize - 10)
  })
  if (state.gameOver) {
    ctx.fillStyle = 'black'
    ctx.font = '30px Arial'
    ctx.fillText('Game Over', ctx.canvas.width / 2 - 70, ctx.canvas.height / 2)
  }
}
