import * as conf from './conf'
import { playerSprites } from './images'
import { TileType, State } from './state'

export const render = (ctx: CanvasRenderingContext2D) => (state: State) => {
  const tileSize = conf.TILESIZE
  ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height)
  for (let y = 0; y < state.gameMap.height; y++) {
    for (let x = 0; x < state.gameMap.width; x++) {
      const tile = state.gameMap.tiles[y][x]

      switch (tile) {
        case TileType.WALL:
          ctx.fillStyle = 'black'
          break
        case TileType.BREAKABLE:
          ctx.fillStyle = 'brown'
          break
        case TileType.WATER:
          ctx.fillStyle = 'blue'
          break
        case TileType.EMPTY:
          ctx.fillStyle = 'gray'
          break
        case TileType.EXPLOSION:
          ctx.fillStyle = 'red'
          break
      }
      ctx.fillRect(x * tileSize, y * tileSize, tileSize, tileSize)
    }
  }

  // Render player
  const playerImg = playerSprites[state.player.direction]
  if (playerImg.complete) {
    const tileSize = conf.TILESIZE
    ctx.drawImage(
      playerImg,
      state.player.x * tileSize,
      state.player.y * tileSize,
      tileSize,
      tileSize
    )
  }

  // Render bombs
  state.bombs.forEach(bomb => {
    ctx.fillStyle = 'yellow'
    ctx.fillRect(bomb.x * tileSize, bomb.y * tileSize, tileSize, tileSize)
  })
  // Render explosions
  state.explosions.forEach(explosion => {
    ctx.fillStyle = 'red'
    ctx.fillRect(explosion.x * tileSize, explosion.y * tileSize, tileSize, tileSize)
  })

  if (state.gameOver) {
    ctx.fillStyle = 'black'
    ctx.font = '30px Arial'
    ctx.fillText('Game Over', ctx.canvas.width / 2 - 70, ctx.canvas.height / 2)
  }
}
