import { playerSprites } from './images';
import { TileType } from './state';
export const render = ctx => state => {
  const tileSize = 40;
  ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);

  for (let y = 0; y < state.gameMap.height; y++) {
    for (let x = 0; x < state.gameMap.width; x++) {
      const tile = state.gameMap.tiles[y][x];

      switch (tile) {
        case TileType.WALL:
          ctx.fillStyle = 'black';
          break;

        case TileType.BREAKABLE:
          ctx.fillStyle = 'brown';
          break;

        case TileType.WATER:
          ctx.fillStyle = 'blue';
          break;

        case TileType.EMPTY:
          ctx.fillStyle = 'white';
          break;

        case TileType.EXPLOSION:
          ctx.fillStyle = 'red';
          break;
      }

      ctx.fillRect(x * tileSize, y * tileSize, tileSize, tileSize);
    }
  } // Render player


  const playerImg = playerSprites[state.player.direction];

  if (playerImg.complete) {
    const tileSize = 40;
    ctx.drawImage(playerImg, state.player.x * tileSize, state.player.y * tileSize, tileSize, tileSize);
  }
};
